from pydantic import Field, SecretStrfrom pyoaev.configuration import ConfigLoaderCollectorclass CollectorConfigOverride(ConfigLoaderCollector):    id: str = Field(        default="openaev_microsoft_intune",        description="Collector unique identifier",    )    name: str = Field(        default="Microsoft Intune",        description="Collector display name",    )    icon_filepath: str | None = Field(        default="microsoft_intune/img/icon-microsoft-intune.png",        description="Path to the icon file",    )    microsoft_intune_tenant_id: str = Field(        description="Azure Active Directory tenant ID for Microsoft Intune.",    )    microsoft_intune_client_id: str = Field(        description="Azure AD application (client) ID for Microsoft Intune.",    )    microsoft_intune_client_secret: SecretStr = Field(        description="Azure AD application client secret for Microsoft Intune.",    )    microsoft_intune_device_filter: str = Field(        description="OData filter for device selection",    )    microsoft_intune_device_groups: str = Field(        description="Comma-separated list of device group names or IDs",    )